svg.script:
// e.g. <dsyim.svg#size=500,color=red,line=gray,lineWidth=3>
const url = new URL(document.URL);
const vals = {};
if(url.hash != "") {
	// skip first char(="#"), and split by comma(=","); replace url encoded #(%23) to #.
	let tmp = url.hash.substr(1).replace("%23","#").split(","); 

	for(x in tmp) {
		// max item = 2 (eg. "gon,is,here".split(",",2) -> ["gon", "is"])
		let tmp2 = tmp[x].split("="); 
		if(tmp2.length>1) { vals[tmp2[0].trim()] = tmp2[1].trim(); }
	}
}

const modSvg2 = (id, param, item) => {
	if(vals[param] != null && vals[param].length > 0) 
		document.getElementById(id).setAttributeNS(null, item, vals[param]); }
const modDom2 = (id, param, item) => {
	if(vals[param] != null && vals[param].length > 0) 
			document.getElementById(id).setAttribute(item, vals[param]); }

modDom2("dsyim-svg", "size", "width");
modDom2("dsyim-svg", "size", "height");
modSvg2("dsyim", "fill", "fill");
modSvg2("dsyim", "stroke", "stroke");
modSvg2("dsyim", "stroke-width", "stroke-width");
